import React, { Component } from "react";
import SavedPost from "./SavedPost";
import Gif from "./Gif";

class WritePost extends Component {
    state = {
        posts: [
            {
                id: 1,
                message: "This is the content...",
                gifUrl: "https://media3.giphy.com/media/28GHfhGFWpFgsQB4wR/giphy.gif?cid=7c5ec064cuhyd5owrtl13x31h2fwn84qqcdwyebgv5u73kx6&rid=giphy.gif&ct=g",
            },
        ],

        gifComponent: false,
        gifInputTxt: "",
    };

    postContent = React.createRef();

    handelFormValue = (event) => {
        console.log(event.target.name, event.target.value);
    };

    handleSubmit = (event) => {
        event.preventDefault();
    };

    deletePost = () => {};

    // handleGifBtn = () => {

    // }

    gifCompToggle = () => {
        let showGifComp = this.state.gifComponent ? false : true;
        this.setState({ gifComponent: showGifComp });
    };

    handleGifSearch = (event) => {
        // console.log(event.target.value);
        this.setState({ gifInputTxt: event.target.value });
    };

    render() {
        console.log(this.state.gifInputTxt);
        return (
            <React.Fragment>
                <div className="container">
                    <div className="row mt-4">
                        <div className="col-md-12">
                            <form onSubmit={this.handleSubmit}>
                                <div className="form-group form-group-lg">
                                    <textarea
                                        className="form-control"
                                        id="txt-area"
                                        rows="3"
                                        onChange={this.handelFormValue}
                                    ></textarea>
                                </div>
                                <div className="d-flex justify-content-between">
                                    <button
                                        className="btn btn-success"
                                        type="button"
                                        onClick={this.gifCompToggle}
                                    >
                                        GIF
                                    </button>
                                    <button
                                        type="submit"
                                        className="btn btn-primary"
                                    >
                                        Submit
                                    </button>
                                </div>
                            </form>
                        </div>
                        <div className="col-md-12">
                            {this.state.gifComponent ? (
                                <Gif handleGifSearch={this.handleGifSearch} />
                            ) : null}
                        </div>
                    </div>
                    <div className="">
                        <SavedPost posts={this.state.posts} />
                    </div>
                </div>
            </React.Fragment>
        );
    }
}

export default WritePost;
