import React, { Component } from "react";
import axios from "axios";

class Gif extends Component {
    state = {
        searchTxt: "",
        gifPosts: [],
    };

    // async componentDidMount() {
    //     // axios return promise object, so we have to use await to wait for the operation to complete means promise to be resolved, and after that execute the remaining instruction
    //     // const apiEndPoint = `https://api.giphy.com/v1/gifs/search?api_key=q0oOTdSJ8s8kiwFMMOsvd5A8CXkwXxv1&q=${this.state.searchTxt}&limit=10&offset=0&rating=g&lang=en`;
    //     // let { data: posts } = await fetch(apiEndPoint);
    //     // posts = await posts.json();
    //     // console.log("fetchApi", posts);
    //     // this.setState({ gifPosts: posts });
    // }

    handleSearch = (event) => {
        this.setState({ searchTxt: event.target.value });
    };

    onSearchClick = async () => {
        // let txt = this.state.searchTxt
        const apiEndPoint = `https://api.giphy.com/v1/gifs/search?api_key=q0oOTdSJ8s8kiwFMMOsvd5A8CXkwXxv1&q=${this.state.searchTxt}&limit=10&offset=0&rating=g&lang=en`;
        console.log(apiEndPoint);
        await fetch(apiEndPoint)
            .then((response) => {
                return response.json();
            })
            .then((response) => {
                console.log("fetchApi", response);
                this.setState({ gifPosts: response.data });
            });

        // let { data: posts } = await fetch(apiEndPoint);
        // posts = await posts.json();
        // console.log("fetchApi", posts);
        // this.setState({ gifPosts: posts });
    };

    renderImages = () => {
        return this.state.gifPosts.map((links) => {
            let srcLink = `https://giphy.com/embed/${links.id}`;
            console.log(links.url);

            <React.Fragment>
                <iframe
                    src
                    srcLink
                    width="200"
                    height="200"
                    frameBorder="0"
                    className="giphy-embed"
                    allowFullScreen
                    key={links.id}
                    title={links.id}
                ></iframe>
                <p>
                    <a href={links.url}>via GIPHY</a>
                </p>
            </React.Fragment>;
        });

        return srcLink;
    };

    render() {
        let { handleGifSearch } = this.props;

        console.log("Posts: ", this.state.gifPosts);
        return (
            <React.Fragment>
                <div className="d-flex">
                    <input
                        type="text"
                        className="form-control"
                        placeholder="Search..."
                        // onChange={handleGifSearch}
                        onChange={this.handleSearch}
                        // value={value}
                    ></input>
                    <button
                        className="btn btn-primary"
                        type="button"
                        onClick={this.onSearchClick}
                    >
                        Search
                    </button>
                </div>
                <div className="d-flex justify-content-start">
                    {this.state.gifPosts.length !== 0
                        ? this.renderImages()
                        : null}
                </div>
            </React.Fragment>
        );
    }
}

export default Gif;
