import React, { Component } from "react";
import SavedPost from "./SavedPost";
import Gif from "./Gif";

class WritePost extends Component {
    state = {
        posts: [
            {
                id: 1,
                message: "This is the content...",
                gifUrl: "https://media3.giphy.com/media/28GHfhGFWpFgsQB4wR/giphy.gif?cid=7c5ec064cuhyd5owrtl13x31h2fwn84qqcdwyebgv5u73kx6&rid=giphy.gif&ct=g",
            },
        ],

        gifComponent: false,
        post: {},
    };

    postContent = React.createRef();

    handelFormValue = (event) => {
        console.log("Form Values: ", event.target.name, event.target.value);
        this.setState({ post: { message: event.target.value } });
    };

    handleGifImg = (imgUrl, event) => {
        event.preventDefault();
        console.log("Image URL: ", imgUrl);
        let post = this.state.post;
        post.gifUrl = imgUrl;
        this.setState({ post });
    };

    handleSubmit = (event) => {
        event.preventDefault();
        console.log("Successfully Submitted...");
        let posts = this.state.posts;
        let dataCount = posts.length;
        let postContent = this.postContent.current.value;
        let post = this.state.post;
        post.id = dataCount + 1;
        post.message = postContent;
        posts.push(post);
        console.log(post);

        // posts.push({})
        this.setState({ posts, post: {} });
    };

    deletePost = () => {};

    // handleGifBtn = () => {

    // }

    gifCompToggle = () => {
        let showGifComp = this.state.gifComponent ? false : true;
        this.setState({ gifComponent: showGifComp });
    };

    render() {
        // console.log(this.state.gifInputTxt);
        return (
            <React.Fragment>
                <div className="container">
                    <div className="row mt-4">
                        <div className="col-md-12">
                            <form onSubmit={this.handleSubmit}>
                                <div className="form-group form-group-lg">
                                    <textarea
                                        className="form-control"
                                        id="txt-area"
                                        rows="3"
                                        onChange={this.handelFormValue}
                                        ref={this.postContent}
                                    ></textarea>
                                </div>
                                <div className="d-flex justify-content-between">
                                    <button
                                        className="btn btn-success"
                                        type="button"
                                        onClick={this.gifCompToggle}
                                    >
                                        GIF
                                    </button>
                                    <button
                                        type="submit"
                                        className="btn btn-primary"
                                    >
                                        Submit
                                    </button>
                                </div>
                            </form>
                        </div>
                        <div className="col-md-12">
                            {this.state.gifComponent ? (
                                <Gif handleGifImg={this.handleGifImg} />
                            ) : null}
                        </div>
                    </div>
                    <div className="">
                        <SavedPost posts={this.state.posts} />
                    </div>
                </div>
            </React.Fragment>
        );
    }
}

export default WritePost;
