import React, { Component } from "react";
import SavedPost from "./SavedPost";

class WritePost extends Component {
    state = {
        posts: [
            {
                message: "This is the content...",
                gifUrl: "https://media3.giphy.com/media/28GHfhGFWpFgsQB4wR/giphy.gif?cid=7c5ec064cuhyd5owrtl13x31h2fwn84qqcdwyebgv5u73kx6&rid=giphy.gif&ct=g",
            },
        ],
    };

    postContent = React.createRef();

    handelFormValue = (event) => {
        console.log(event.target.name, event.target.value);
    };

    handleSubmit = (event) => {
        event.preventDefault();
    };

    deletePost = () => {};

    render() {
        return (
            <React.Fragment>
                <div className="container">
                    <div className="row mt-4">
                        <div className="col-md-12">
                            <form onSubmit={this.handleSubmit}>
                                <div className="form-group form-group-lg">
                                    <textarea
                                        className="form-control"
                                        id="txt-area"
                                        rows="3"
                                        onChange={this.handelFormValue}
                                    ></textarea>
                                </div>
                                <button
                                    type="submit"
                                    className="btn btn-primary"
                                >
                                    Submit
                                </button>
                            </form>
                        </div>
                    </div>
                    <div className="">
                        <SavedPost posts={this.state.posts} />
                    </div>
                </div>
            </React.Fragment>
        );
    }
}

export default WritePost;
